package day18_do_while_while_loops;

public class Staircase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	String stairs="*";
	System.out.println(stairs);
	stairs+="*";
	
	while(stairs.length()<=20) {
		System.out.println(stairs);
		stairs+="*";
	}
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
